mapper.select:
		
		
			
				AND title like concat('%', replace(#{title}::text, '_', '\_'), '%')
			
			
				AND business_type = #{businessType}
			
			
			    AND business_type in
			    
		 			#{businessType}
		         
			
			
				AND status = #{status}
			
			
				AND oper_name like concat('%', replace(#{operName}::text, '_', '\_'), '%')
			

			
				AND to_date(cast(oper_time as TEXT),'YYYY-MM-DD HH24:MI:SS')  >=  to_date(cast(#{params.beginTime} as TEXT),'YYYY-MM-DD HH24:MI:SS')
			
			
				AND to_date(cast(oper_time as TEXT),'YYYY-MM-DD HH24:MI:SS')  <=  to_date(cast(#{params.endTime} as TEXT),'YYYY-MM-DD HH24:MI:SS')
			
		
		order by oper_id desc
